import "./styles.css";
import { useState } from 'react';

// state is at component level, not global.
// state should be set at component level where its defined, pass function that updaes state to child if needed.

function Square({square, onClick, player}) {
  const disabled = (square === null ? false : true);
  return <button
   className="square" onClick={onClick} disabled={disabled}
   >{square}</button>;
};

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

function Board({player, onPlay, squares}) {

  if (calculateWinner(squares)) {
      status = calculateWinner(squares) + " won";
  } else {
    status ="Next player = " + player;
  }

  function onSquareClick(squareNumber, player) {
    const nextSquares = squares.slice();
    nextSquares[squareNumber] = player;
    onPlay(nextSquares);
  };

  const indexes = []
  const arr = [...Array(9).keys()];

  console.log(arr);

  const rows = [];
  for (let row = 0; row < 3; row++) {
    const squaresInRow = [];
    for (let col = 0; col < 3; col++) {
      const index = row * 3 + col;
      squaresInRow.push(
        <Square
          key={index}
          square={squares[index]}
          onClick={() => onSquareClick(index, player)}
        />
      );
    }
    rows.push(
      <div className="board-row" key={row}>
        {squaresInRow}
      </div>
    );
  }
  return rows;

  return <>
    <div className="status">
      {status}
    </div>
    <div className='board-row'>
      <Square square={squares[0]} onClick={() => onSquareClick(0, player)} />
      <Square square={squares[1]} onClick={() => onSquareClick(1, player)} />
      <Square square={squares[2]} onClick={() => onSquareClick(2, player)} />
    </div>
    <div className='board-row'>
      <Square square={squares[3]} onClick={() => onSquareClick(3, player)} />
      <Square square={squares[4]} onClick={() => onSquareClick(4, player)} />
      <Square square={squares[5]} onClick={() => onSquareClick(5, player)} />
    </div>
    <div className='board-row'>
      <Square square={squares[6]} onClick={() => onSquareClick(6, player)} />
      <Square square={squares[7]} onClick={() => onSquareClick(7, player)} />
      <Square square={squares[8]} onClick={() => onSquareClick(8, player)} />
    </div>


  </>;
}

export default function Game() {
  const [currentMove, setCurrentMove] = useState(0);
  const player = (currentMove % 2 === 0 ? "X" : "0");
  const [history, setHistory] = useState([Array(9).fill(null)]);

  function handlePlay(nextSquares) {
    const nextHistory = [...history.slice(0, currentMove + 1), nextSquares];
    setHistory(nextHistory);
    setCurrentMove(nextHistory.length - 1);
  }

  function jumpTo(move) {
    // update history, not present in tutorial 
    setHistory(history.slice(0, move + 1));
    // set move number
    setCurrentMove(move);
  }

  const allMoves = history.map((squares, move) => {
      let description;
      if (move > 0) {
        description = 'Go to move #' + move;
      } else {
        description = 'Go to game start';
      }

      return (
        <li key={move}>
          <button onClick={() => jumpTo(move)}>{description}</button>
        </li>
      );
  });

  const currentSquares = history[currentMove];

  return (
    <div className="game">
      <div className="game-board">
        <Board player={player} onPlay={handlePlay} squares={currentSquares}/>
      </div>
      <div className="game-info">
        <ol>{allMoves}</ol>
      </div>
    </div>
  );
}
