function Square({square, onClick, player}) {
  const disabled = (square !== null);
  return <button
   className="square" onClick={onClick} disabled={disabled}
   >{square}</button>;
};

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

function Board({player, history}) {
  const [squares, setSquares] = useState(Array(9).fill(null));

  if (calculateWinner(squares)) {
      status = calculateWinner(squares) + " won";
  } else {
    status = player + " turn";
  }

  function onSquareClick(squareNumber, player) {
    const nextSquares = squares.slice();
    nextSquares[squareNumber] = player;
    setSquares(nextSquares);

    const nextTurnPlayer = (player === "X") ? "O" : "X";
    console.log(nextTurnPlayer);
    setPlayer(nextTurnPlayer);
  };

  return <>
    <div className='board-row'>
      <Square square={squares[0]} onClick={() => onSquareClick(0, player)} />
      <Square square={squares[1]} onClick={() => onSquareClick(1, player)} />
      <Square square={squares[2]} onClick={() => onSquareClick(2, player)} />
    </div>
    <div className='board-row'>
      <Square square={squares[3]} onClick={() => onSquareClick(3, player)} />
      <Square square={squares[4]} onClick={() => onSquareClick(4, player)} />
      <Square square={squares[5]} onClick={() => onSquareClick(5, player)} />
    </div>
    <div className='board-row'>
      <Square square={squares[6]} onClick={() => onSquareClick(6, player)} />
      <Square square={squares[7]} onClick={() => onSquareClick(7, player)} />
      <Square square={squares[8]} onClick={() => onSquareClick(8, player)} />
    </div>
    <div className="status">
      {status}
    </div>
  </>;
}

export default function Game() {
  const [player, setPlayer] = useState("X");
  const [history, setHistory] = useState([Array(9).fill(null)]);

  function handlePlay(nextSquares) {
    setHistory([...history, nextSquares]);
  }

  return (
    <div className="game">
      <div className="game-board">
        <Board player={player} history={history}/>
      </div>
      <div className="game-info">
        <ol>{/*TODO*/}</ol>
      </div>
    </div>
  );
}
