import "./styles.css";
import { useState } from 'react';


const [player, setPlayer] = useState("X");

function Square({player}) {
  const [value, setValue] = useState(null);
  function handleClick() {
    console.log('clicked');
    if (player === '1'){
      setValue("X");

    }
    else {
      setValue("O");
    }
    const nextTurnPlayer = (player === "X") ? "O" : "X";
    setPlayer(nextTurnPlayer);
  }
  return <button className="square" onClick={handleClick}>{value}</button>;
};

export default function Board() {
  return <>
    <div className='board-row'>
      <Square number={"1"}/>
      <Square number={"2"}/>
      <Square number={"3"}/>
    </div>
    <div className='board-row'>
      <Square number={"4"}/>
      <Square number={"5"}/>
      <Square number={"6"}/>
    </div>
    <div className='board-row'>
      <Square number={"7"}/>
      <Square number={"8"}/>
      <Square number={"9"}/>
    </div>
  </>;
}
