export default function Board() {
  const [player, setPlayer] = useState("X");
  const [squares, setSquares] = useState(Array(9).fill(null));

  function Square({square, onClick, player}) {

    const [disabled, setDisabled] = useState(false);

    // function handleClick() {
    //   console.log('clicked');
    //   setValue(player);
    //   console.log(value);

    //   const nextTurnPlayer = (player === "X") ? "O" : "X";
    //   console.log(nextTurnPlayer);
    //   setPlayer(nextTurnPlayer);
    //   setDisabled(true);
    // }

    return <button
     className="square" onClick={onClick} disabled={disabled}
     >{square}</button>;
  };

  function onSquareClick(squareNumber) {
    const nextSquares = squares.slice();
    nextSquares[squareNumber] = "X";
    setSquares(nextSquares);
  };

  return <>
    <div className='board-row'>
      <Square square={squares[0]} onClick={onSquareClick(0)} />
      <Square square={squares[1]} onClick={onSquareClick(1)} />
      <Square square={squares[2]} onClick={onSquareClick(2)} />
    </div>
    <div className='board-row'>
      <Square square={squares[3]} onClick={onSquareClick(3)} />
      <Square square={squares[4]} onClick={onSquareClick(4)} />
      <Square square={squares[5]} onClick={onSquareClick(5)} />
    </div>
    <div className='board-row'>
      <Square square={squares[6]} onClick={onSquareClick(6)} />
      <Square square={squares[7]} onClick={onSquareClick(7)} />
      <Square square={squares[8]} onClick={onSquareClick(8)} />
    </div>
  </>;
}
