export default function Board() {
  const [player, setPlayer] = useState("X");

  function Square({player}) {

    const [value, setValue] = useState(null);
    const [disabled, setDisabled] = useState(false);

    function handleClick() {
      console.log('clicked');
      setValue(player);
      console.log(value);

      const nextTurnPlayer = (player === "X") ? "O" : "X";
      console.log(nextTurnPlayer);
      setPlayer(nextTurnPlayer);
      setDisabled(true);
    }

    return <button
     className="square" onClick={handleClick} disabled={disabled}
     >{value}</button>;
  };

  return <>
    <div className='board-row'>
      <Square player={player}/>
      <Square player={player}/>
      <Square player={player}/>
    </div>
    <div className='board-row'>
      <Square player={player}/>
      <Square player={player}/>
      <Square player={player}/>
    </div>
    <div className='board-row'>
      <Square player={player}/>
      <Square player={player}/>
      <Square player={player}/>
    </div>
  </>;
}
